mapper.update:
update t_token
set userid = #{userid,jdbcType=INTEGER},
  pwd_token = #{pwdToken,jdbcType=VARCHAR},
  auto_login_token = #{autoLoginToken,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, userid, pwd_token, auto_login_token
from t_token
where id = #{id,jdbcType=INTEGER}
